function findFirstIncompleteNodeId(data) {
  if (!Array.isArray(data)) {
    console.log("Input must be an array.");
    return null;
  }

  const queue = [...data];

  while (queue.length > 0) {
    const node = queue.shift();

    if (node === null || typeof node !== 'object' || !node.hasOwnProperty('id')) {
      console.log("Skipping invalid node:", node);
      continue;
    }

    if (!node.hasOwnProperty('children')) {
      return node.id;
    }

    if (Array.isArray(node.children) && node.children.length > 0) {
      for (const child of node.children) {
         if (child !== null && typeof child === 'object') {
             queue.push(child);
         } else {
            console.log("Skipping invalid child node:", child, "under parent:", node.id);
         }
      }
    }
  }
  return null;
}

