  if (node.id === id_to_insert_children_in) {
    // If the node's ID matches, insert the children
    node.children = children_to_insert;
    return true; // Indicate that the insertion was successful
  }

  // If the node has children, recursively check them
  if (node.children && Array.isArray(node.children)) {
