function notionToMarkdown(blocks) {
  return blocks
    .map(block => {
      if (!block.json.content) return ""; // Skip empty content

      switch (block.json.type) {
        case "heading_1":
          return `# ${block.json.content}`;
        case "heading_2":
          return `## ${block.json.content}`;
        case "heading_3":
          return `### ${block.json.content}`;
        case "bulleted_list_item":
          return `- ${block.json.content}`;
        case "to_do":
          return `- [ ] ${block.json.content}`;
        case "paragraph":
          return `${block.json.content}`;
        default:
          return ""; // Ignore unsupported types
      }
    })
    .filter(line => line.trim() !== "") // Remove empty lines
    .join("\n\n"); // Ensure proper spacing
}
