// Helper function to convert markdown to CSV
function markdownToCSV(mdContent) {
  const lines = mdContent.split("\n\n");
  const header = ['Type', 'Content'];
  const rows = lines.map(line => {
    if (line.startsWith("# ")) {
      return ['Heading 1', line.replace('# ', '')];
    } else if (line.startsWith("## ")) {
      return ['Heading 2', line.replace('## ', '')];
    } else if (line.startsWith("### ")) {
      return ['Heading 3', line.replace('### ', '')];
    } else if (line.startsWith("- ")) {
      return ['Bulleted List Item', line.replace('- ', '')];
    } else {
      return ['Paragraph', line];
    }
  });
  const csvContent = [header, ...rows].map(row => row.join(',')).join("\n");
  return csvContent;
}

// Helper function to convert markdown to HTML
function markdownToHTML(mdContent) {
  const lines = mdContent.split("\n\n");
  let htmlContent = '';

  lines.forEach(line => {
    if (line.startsWith("# ")) {
      htmlContent += `<h1>${line.replace('# ', '')}</h1>`;
    } else if (line.startsWith("## ")) {
      htmlContent += `<h2>${line.replace('## ', '')}</h2>`;
    } else if (line.startsWith("### ")) {
      htmlContent += `<h3>${line.replace('### ', '')}</h3>`;
    } else if (line.startsWith("- ")) {
      htmlContent += `<ul><li>${line.replace('- ', '')}</li></ul>`;
    } else {
      htmlContent += `<p>${line}</p>`;
    }
  });

  return `<html><body>${htmlContent}</body></html>`;
}

// Helper function to convert markdown to JSON
function markdownToJSON(mdContent) {
  const lines = mdContent.split("\n\n");
  return lines.map(line => {
    if (line.startsWith("# ")) {
      return { type: 'heading_1', content: line.replace('# ', '') };
    } else if (line.startsWith("## ")) {
      return { type: 'heading_2', content: line.replace('## ', '') };
    } else if (line.startsWith("### ")) {
      return { type: 'heading_3', content: line.replace('### ', '') };
    } else if (line.startsWith("- ")) {
      return { type: 'bulleted_list_item', content: line.replace('- ', '') };
    } else {
      return { type: 'paragraph', content: line };
    }
  });
}

// Helper function to convert markdown to plain text (TXT)
function markdownToTXT(mdContent) {
  return mdContent.replace(/\n\n/g, '\n').replace(/#/g, '').replace(/-/g, '').trim();
}

// Helper function to convert markdown to XLS/XLSX (simulated as JSON in this case)
function markdownToXLSX(mdContent) {
  const lines = mdContent.split("\n\n");
  return lines.map(line => {
    if (line.startsWith("# ")) {
      return { Type: 'Heading 1', Content: line.replace('# ', '') };
    } else if (line.startsWith("## ")) {
      return { Type: 'Heading 2', Content: line.replace('## ', '') };
    } else if (line.startsWith("### ")) {
      return { Type: 'Heading 3', Content: line.replace('### ', '') };
    } else if (line.startsWith("- ")) {
      return { Type: 'Bulleted List Item', Content: line.replace('- ', '') };
    } else {
      return { Type: 'Paragraph', Content: line };
    }
  });
}

let output = {};

if (markdownInput) {
  const mdContent = markdownInput

  switch (ext) {
    case 'csv':
      // Convert to CSV format
      const csvContent = markdownToCSV(mdContent);
      output = {
        fileType: 'csv',
        fileName: name,
        fileContent: csvContent
      };
      break;
    case 'html':
      // Convert to HTML format
      const htmlContent = markdownToHTML(mdContent);
      output = {
        fileType: 'html',
        fileName: name,
        fileContent: htmlContent
      };
      break;
    case 'json':
      // Convert to JSON format
      const jsonContent = markdownToJSON(mdContent);
      output = {
        fileType: 'json',
        fileName: name,
        fileContent: JSON.stringify(jsonContent, null, 2)
      };
      break;
    case 'txt':
      // Convert to TXT format
      const txtContent = markdownToTXT(mdContent);
      output = {
        fileType: 'txt',
        fileName: name,
        fileContent: txtContent
      };
      break;
    case 'xls':
    case 'xlsx':
      // Convert to XLS/XLSX (simulated as JSON)
      const xlsxContent = markdownToXLSX(mdContent);
      output = {
        fileType: ext,
        fileName: name,
        fileContent: JSON.stringify(xlsxContent)
      };
      break;
    default:
      // Default case: Convert to JSON if the extension is unsupported
      const defaultJsonContent = markdownToJSON(mdContent);
      output = {
        fileType: 'json-other',
        fileName: name.replace(/\.[^/.]+$/, '.json-other'), // Replace the extension with .json
        fileContent: JSON.stringify(defaultJsonContent, null, 2)
      };
      break;
  }
} else {
  output = {
    fileType: 'json',
    fileName: name,
    fileContent: JSON.stringify({ message: 'No markdown input provided' })
  };
}

return [{ json: output }];

