const items = $input.all();

return items.map(item => {
  const {
    mimeType,
    webViewLink,
    relativePath,
    drive_name,
    drive_id,
    data
  } = item.json;

  // if theres no data array or its empty, return metadata only
  if (!Array.isArray(data) || data.length === 0) {
    return {
      json: {
        mimeType,
        webViewLink,
        relativePath,
        drive_name,
        drive_id,
        blocks: []
      }
    };
  }

  // derive table headers from the first rows keys
  const headers = Object.keys(data[0]);

  // build the header row
  const headerRow = {
    object: 'block',
    type: 'table_row',
    table_row: {
      cells: headers.map(h => ([{
        type: 'text',
        text: { content: h }
      }]))
    }
  };

  // build one data row per entry
  const dataRows = data.map(row => ({
    object: 'block',
    type: 'table_row',
    table_row: {
      cells: headers.map(h => ([{
        type: 'text',
        text: { content: row[h] != null ? String(row[h]) : '' }
      }]))
    }
  }));

  // assemble the table block
  const tableBlock = {
    object: 'block',
    type: 'table',
    table: {
      table_width: headers.length,
      has_column_header: true,
      has_row_header: false,
      children: [headerRow, ...dataRows]
    }
  };

  return {
    json: {
      mimeType,
      webViewLink,
      relativePath,
      drive_name,
      drive_id,
      blocks: [tableBlock]
    }
  };
});

