return $input.all().map(item => {
  const {
    mimeType,
    webViewLink,
    relativePath,
    drive_name,
    drive_id,
    data
  } = item.json;

  const blocks = [];

  // only use the data array for processing
  (Array.isArray(data) ? data : []).forEach(row => {
    // heading with the fileName
    blocks.push({
      object: 'block',
      type: 'heading_2',
      heading_2: {
        rich_text: [{
          type: 'text',
          text: { content: row.fileName }
        }],
        is_toggleable: false
      }
    });

    // parse the JSON content string
    let parsed = [];
    try {
      parsed = JSON.parse(row.fileContent);
    } catch (err) {
      blocks.push({
        object: 'block',
        type: 'paragraph',
        paragraph: {
          rich_text: [{
            type: 'text',
            text: { content: ` Failed to parse JSON: ${err.message}` }
          }]
        }
      });
      return; // skip further processing of this row
    }

    // convert each parsed entry into a Notion paragraph block
    parsed.forEach(entry => {
      if (entry.type === 'paragraph') {
        blocks.push({
          object: 'block',
          type: 'paragraph',
          paragraph: {
            rich_text: [{
              type: 'text',
              text: { content: entry.content }
            }]
          }
        });
      }
      // add more handlers here if needed (e.g., headings, lists, etc.)
    });
  });

  // return metadata plus the generated blocks
  return {
    json: {
      mimeType,
      webViewLink,
      relativePath,
      drive_name,
      drive_id,
      blocks
    }
  };
});

