  // build Notion blocks: start with a heading naming the file
  const blocks = [{
    object: 'block',
    type: 'heading_2',
    heading_2: {
      rich_text: [{
        type: 'text',
        text: { content: `Text File ${index + 1}` }
      }],
      is_toggleable: false
    }
  }];

  // one paragraph block per line of text
  lines.forEach(line => {
    blocks.push({
      object: 'block',
      type: 'paragraph',
      paragraph: {
        rich_text: [{
          type: 'text',
          text: { content: line }
        }]
      }
    });
  });

  // return metadata plus the generated blocks
  return {
    json: {
      mimeType,
      webViewLink,
      relativePath,
      drive_name,
      drive_id,
      blocks
    }
  };
});

