// process each incoming item, decode its base64 data to HTML,
// include metadata (mimeType, webViewLink, relativePath, drive_name, drive_id)
return $input.all().map(item => {
  const {
    mimeType,
    webViewLink,
    relativePath,
    drive_name,
    drive_id,
    data
  } = item.json;

  // decode only the 'data' field (base64-encoded HTML)
  const base64 = data || '';
  const buffer = Buffer.from(base64, 'base64');
  const html = buffer.toString('utf8');

  return {
    json: {
      mimeType,
      webViewLink,
      relativePath,
      drive_name,
      drive_id,
      html
    }
  };
});
