          const inputs = question.querySelectorAll('input');
          let selectedValues = [];
          let correctValues = [];
          inputs.forEach(input => {
            if (input.checked) selectedValues.push(input.value);
            if (input.dataset.correct === "true") correctValues.push(input.value);
          });
          if (
            selectedValues.length !== correctValues.length ||
            !selectedValues.every(val => correctValues.includes(val))
          ) { correct = false; }
          if (correct) {
            totalPoints += points;
            question.classList.add('correct');
          } else {
            question.classList.add('incorrect');
            inputs.forEach(input => {
              if (input.dataset.correct === "true") {
                input.parentElement.classList.add('correct-answer');
              }
            });
          }
        });
        const percentage = ((totalPoints / maxPoints) * 100).toFixed(1);
