      /**
       * parseMarkdownAdvanced(markdownText):
       *  1) Converts headings (#, ##, ###, ####) into collapsible <details> elements with appropriate heading tags and level classes.
       *     For example: <details class="level-1"><summary><h1>Heading</h1></summary> ... </details>
       *  2) Converts code blocks (``` ... ```) into <pre><code>...</code></pre>.
       *  3) Lines starting with "- " or "* " become list items grouped in <ul>.
       *  4) **bold** becomes <strong> and _italic_ becomes <em>.
       *  5) All other lines are wrapped in <p>.
       */
      function parseMarkdownAdvanced(markdownText) {
        const lines = markdownText.split('\n');
        let segments = [];
        let isCodeBlock = false;
        let currentBlock = [];
        for (let line of lines) {
          if (line.trim().startsWith('```')) {
            if (!isCodeBlock) {
              isCodeBlock = true;
              continue;
            } else {
              isCodeBlock = false;
              segments.push({ type: 'code', content: currentBlock.join('\n') });
              currentBlock = [];
              continue;
            }
          }
          if (isCodeBlock) {
            currentBlock.push(line);
          } else {
            segments.push({ type: 'text', content: line });
          }
        }
        let html = '';
        let openDetailsStack = [];
        let isListOpen = false;
        function closeDetailsUntil(level) {
          while (openDetailsStack.length && openDetailsStack[openDetailsStack.length - 1] >= level) {
            html += '</details>';
            openDetailsStack.pop();
          }
        }
        function closeListIfNeeded() {
          if (isListOpen) {
            html += '</ul>';
            isListOpen = false;
          }
        }
        function inlineFormat(str) {
          str = str.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;');
          str = str.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
          str = str.replace(/\_(.+?)\_/g, '<em>$1</em>');
          return str;
        }
        segments.forEach(seg => {
          if (seg.type === 'code') {
            closeDetailsUntil(0);
            closeListIfNeeded();
            let codeContent = seg.content.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;');
            html += `<pre><code>${codeContent}</code></pre>`;
          } else {
            let line = seg.content;
            let trimmed = line.trim();
            if (!trimmed) { closeListIfNeeded(); return; }
            let headingMatch = trimmed.match(/^(#{1,4})\s+(.*)/);
            if (headingMatch) {
              closeListIfNeeded();
              const level = headingMatch[1].length;
              const headingText = headingMatch[2];
              closeDetailsUntil(level);
              const headingTag = `h${level}`;
              html += `<details class="level-${level}"><summary><${headingTag}>${inlineFormat(headingText)}</${headingTag}></summary>`;
              openDetailsStack.push(level);
              return;
            }
            if (/^(\-|\*)\s+/.test(trimmed)) {
              if (!isListOpen) { html += '<ul>'; isListOpen = true; }
              let itemText = trimmed.replace(/^(\-|\*)\s+/, '');
              html += `<li>${inlineFormat(itemText)}</li>`;
              return;
            }
            closeListIfNeeded();
            html += `<p>${inlineFormat(trimmed)}</p>`;
          }
        });
        closeListIfNeeded();
        closeDetailsUntil(0);
        return html;
      }
    });
