function parseDateDDMMYYYYHHmm(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') return null;

  // Отделяем дату и время
  const [datePart, timePart] = dateStr.split(" ");
  if (!datePart) return null;

  // Разделяем дату по точке или слешу
  let [day, month, year] = datePart.split(/[./]/);

  // Если год записан двумя цифрами, считаем 20xx
  if (year && year.length === 2) {
    year = `20${year}`;
  }

  let hours = 0;
  let minutes = 0;
  if (timePart) {
    const [hh, mm] = timePart.split(":");
    hours = parseInt(hh, 10) || 0;
    minutes = parseInt(mm, 10) || 0;
  }

  const parsedDate = new Date(
    parseInt(year, 10),
    parseInt(month, 10) - 1,
    parseInt(day, 10),
    hours,
    minutes
  );
  if (isNaN(parsedDate.getTime())) return null;

  return parsedDate;
}

// Функция проверки совпадения дня, месяца и года
